function loadWeather(e,t){$.simpleWeather({location:e,woeid:t,unit:"C",success:function(e){temp="<p>"+e.temp+"&deg;C</p>",wcode='<object class="icon" data="assets/img/svg/weathericons/'+e.code+'.svg">',cur="<p>"+e.currently+"</p>",winds="<p>"+e.wind.speed+e.units.speed+"</p>",windd="<p>"+e.wind.direction+"</p>",sunu="<p>"+e.sunrise+"</p>",sund="<p>"+e.sunset+"</p>",visi="<p>"+e.visibility+e.units.distance+"</p>",$(".temperature").html(temp),$(".climate_bg").html(wcode),$(".windspeed").html(winds),$(".direction").html(windd),$(".currently").html(cur),$(".sun-up").html(sunu),$(".sun-down").html(sund),$(".visibility").html(visi)},error:function(e){$(".error").html("<p>"+e+"</p>")}})}loadWeather("Fort William, Scotland"),$(document).ready(function(){setInterval(loadWeather,1e4)});